import React, {useState} from 'react';
// import Meetups from './Meetups';
import Chris from './images/chris.jpeg';
import './App.scss';

function App() {
  const [members, setMembers] = useState([
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
    {
      name: 'Chris',
      src: Chris,
    },
  ]);
  return (
    <div style={{margin: '0', padding: '0'}}>
      <header>
        <h1>BendJS</h1>
      </header>

      <main>
        {/* <div className="module module-next-meetup" id="module-next-meetup"><Meetups /></div> */}
        <div className="module">
          <h3>Say Something</h3>
          <h2>We're always looking for speakers</h2>
          <p>If you have something to share, big or small, please get in touch!</p>
          <a href="mailto:bendjsgroup@gmail.com" className="button">ðŸ“§ bendjsgroup@gmail.com</a>
        </div>

        <div className="module">
          <h3>Youtube</h3>
          <h2>See us in Action</h2>
          <p>Catch up on old events or watch our Livestreams if you can't make it</p>
          <a href="https://www.youtube.com/channel/UCCT744K1sGX-lPtaEivXCDQ" className="button">Youtube Channel</a>
        </div>

        <div className="module module-next-meetup">
          <h3>Newsletter</h3>
          <h2>Stay up to date</h2>
          <p>Join our newsletter to get up-to-date info about our community</p>
          <div className="row">
            <input type="text" className="signup" placeholder="YOUR EMAIL" />
            <button className="button" onClick={() => null}>Sign up</button>
          </div>
        </div>

        <div className="module">
          <h3>Chat</h3>
          <h2>Join our community</h2>
          <p>Our Slack group helps us all stay in touch, and our meetup page gives you more info about our events</p>
          <a href="https://join.slack.com/t/bendjs/shared_invite/enQtNjA1MTIyODI0MjYwLWNmODA5OTM3ODJiYTllYWJlNzE0MGUwM2JkOGEyZTllOGNhNmU3YzdkNDE2ZTM1M2M0NDhmYTNkZGE3ZTA5M2Y" className="button" style={{marginBottom: '3px'}}>Get Slack Invite</a>
          <a href="https://www.meetup.com/BendJS/" className="button">Join BendJS</a>
        </div>

        <div className="module">
          <h3>Classifieds</h3>
          <h2>Get / Post a job</h2>
          <p>Looking for a new opportunity or looking for some talent for your company? Let us know and we'll post it here for you!</p>
          <a href="" className="button" style={{marginBottom: '3px'}}>Job Listings</a>
          <a href="mailto:bendjsgroup@gmail.com" className="button">Post a job</a>
        </div>

        <div className="module module-next-meetup">
          <h3>Our Members</h3>
          <h2>Meet our amazing community</h2>
          <p>Our community is everything! Meet the people that make BendJS what it is!</p>
          <div className="members">
            {members.map(m => <img className="avatar" src={m.src} alt="member picture" />)}
          </div>
        </div>

      </main>
      <footer></footer>
    </div>
  );
}

export default App;
